#!/bin/bash

# Set the execution limit for all test cases (if not performing coverage).
ulimit -t 2

# Find the directory that this test script belongs to.
DIR=$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )

# Sets up the necessary environmental variables for the positive test cases.
setup_good()
{
  export HOME="."
  export DISPLAY=""
}

# Sets up the necessary environmental variables for the negative test case.
setup_bad()
{
  export DISPLAY=""
  export HOME=""
}

# Execute the requested test case.
case $2 in
  p1)
    setup_good
    bash -c "$1 -h" |& diff $DIR/test/output.help - && exit 0;;
  p2)
    setup_good
    bash -c "$1" |& diff $DIR/test/output.good - && exit 0;;
  n1)
    setup_bad
    bash -c "$1" |& diff $DIR/test/output.bad - && exit 0;;
esac 
exit 1
